def reverse_init_configuration_element_reporting_fields_data_general_stationary_combustion(apps, schema_monitor):
    ConfigurationElement = apps.get_model('reporting', 'ConfigurationElement')
    Activity = apps.get_model('registration', 'Activity')

    ConfigurationElement.reporting_fields.through.objects.filter(
        configurationelement_id__in=ConfigurationElement.objects.filter(
            activity_id=Activity.objects.get(name='General stationary combustion excluding line tracing').id
        ).values_list('id', flat=True)
    ).delete()


